sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

 // Check if the request contains a body (POST data)
    var requestBody = request.body.data;
    if (requestBody) {
        // Parse the JSON data from the request body
        //var requestData = JSON.parse(requestBody);

        // Here you can process the requestData as needed (e.g., insert into a table)
        // For example, if you have a table named "incident," you can insert a new record:
        var gr = new GlideRecord('x_1139630_projects_user');
        gr.initialize();
		gr.number = requestBody.number;
        gr.name = requestBody.name;
        gr.username = requestBody.username;
		gr.password = requestBody.password;
        var newUserId = gr.insert();

        // Send a response back to the client
        response.setStatus(201); // 201 Created
        response.setBody({ "message": "user created", "sys_id": newUserId });
    } else {
        response.setStatus(400); // 400 Bad Request
        response.setBody({ "error": "Invalid request data" });
    }


})(request, response);
