sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var recordSysId = request.pathParams.sys_id; // Assuming you pass sys_id as a path parameter

    // Check if the record exists with the specified sys_id
    var project = "x_1139630_projects_user"; // Replace with your actual table name
    var record = new GlideRecord(project);

    if (record.get(recordSysId)) {
        var responseData = {
            sys_id: record.sys_id.toString(),
            name: record.name.toString(),
			number: record.number.toString(),
			username: record.username.toString(),
			password: record.password.toString(),
            // Add more fields as needed
        };

        // Send the record as the API response
        response.setBody({message:"user get successfully...", status: 200 ,responseData});
        response.setStatus(200);
    } else {
        response.setStatus(404); // 404 Not Found (Record not found)
        response.setBody({ "error": "Record not found" });
    }
})(request, response);
