sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here

 var requestBody = request.body.data;
    if (requestBody) {
        //var requestData = JSON.parse(requestBody);

        // Check if a user exists with the specified sys_id or username
        var user = "x_1139630_projects_user"; // Replace with your actual table name
        var record = new GlideRecord(user);
		if (requestBody.sys_id) {
            record.addQuery('sys_id', requestBody.sys_id);
        }
         record.query();

        if (record.next()) {
            // Update record attributes
            record.setValue('name', requestBody.name);
			record.setValue('username', requestBody.username);
			record.setValue('password', requestBody.password);
            // Add more fields as needed

            // Commit the changes
            record.update();
            response.setStatus(204); // 204 No Content (Successful update)
			response.setBody({"message": "user updated successfully..." });
        } else {
            response.setStatus(404); // 404 Not Found (Record not found)
            response.setBody({ "error": "Record not found" });
        }
    } else {
        response.setStatus(400); // 400 Bad Request
        response.setBody({ "error": "Invalid request data" });
    }
})(request, response);
